const raw = $input.first().json.text;

// Tenta localizar o in√≠cio e fim do array JSON
const start = raw.indexOf('[\n  {');
const end = raw.lastIndexOf('}\n]');

if (start === -1 || end === -1) {
  throw new Error("N√£o foi poss√≠vel localizar o bloco JSON no texto.");
}

// Extrai trecho do JSON bruto
const jsonBlock = raw.slice(start, end + 3);

// Tenta converter para objeto JS
let parsed;
try {
  parsed = JSON.parse(jsonBlock);
} catch (err) {
  throw new Error("Erro ao fazer parse do JSON: " + err.message);
}

// Retorna no padr√£o de campos customizados
return parsed.map(item => ({
  json: {
    text: "new value",
    array: [item.nome, item.titulo, item.url, item.resumo],
    object: {
      innerKey1: "new value",
      innerKey2: item.nome,
      innerKey3: item.titulo,
      innerKey4: item.url,
      innerKey5: item.resumo
    }
  }
}));

if (!$json.output) {
  throw new Error("Campo 'output' n√£o encontrado. Verifique o node anterior.");
}

const texto = $json.output;

const instagram = texto.split("**Instagram:**\n\n")[1]?.split("\n\n**Twitter:**")[0]?.replace(/\"/g, '').trim() || '';
const twitter = texto.split("**Twitter:**\n\n")[1]?.split("\n\n**Facebook:**")[0]?.replace(/\"/g, '').trim() || '';
const facebook = texto.split("**Facebook:**\n\n")[1]?.split("\n\n**Blog WordPress:**")[0]?.replace(/\"/g, '').trim() || '';
const wordpress = texto.split("**Blog WordPress:**\n\n")[1]?.trim() || '';

return [
  { json: { plataforma: 'Instagram', conteudo: instagram } },
  { json: { plataforma: 'Twitter', conteudo: twitter } },
  { json: { plataforma: 'Facebook', conteudo: facebook } },
  { json: { plataforma: 'WordPress', conteudo: wordpress } }
];
