const input = $json;
const phone = input.phone;
const order = input.order;
const history = input.history;

const now = new Date();
const cutoff = new Date(now.getTime() - 15 * 24 * 60 * 60 * 1000);

let clientHistory = history[phone] || [];
clientHistory = clientHistory.filter(h => new Date(h.timestamp) >= cutoff);

clientHistory.push({
  timestamp: now.toISOString(),
  order: order
});

history[phone] = clientHistory;

